import { useAppSelector } from '../hooks/storeHooks';
import { RootState } from '../store';
import { Post } from './Post';

export function Table() {
  const posts = useAppSelector((state: RootState) => state.posts.posts);
  const tableHeaderTitles: string[] = [
    'ID', 'Заголовок', 'Описание'
  ];

  return (
    <table className="">
      <thead>
        <tr>
          {
            tableHeaderTitles.length && tableHeaderTitles.map(title => {
              return <th className={title === 'ID' ? 'w-1/5' : 'w-2/5'} scope="col">{title}</th>;
            })
          }
        </tr>
      </thead>
      <tbody>
        {
          posts.length && posts.map((post, idx) => {
            return <Post post={post} key={idx} />
          })
        }
      </tbody>
    </table>
  );
}
