import { config } from "../config.d";

export function defineNavBarLimits(postPageCount: number, maxPagesCount: number) {
  const halfNavBarPageButtonsCount = Math.floor(config.maxNavBarPageButtonsCount / 2);
  const navBarLimits = {
    start: +postPageCount - halfNavBarPageButtonsCount - 1,
    end: +postPageCount + halfNavBarPageButtonsCount
  }

  if (+postPageCount - halfNavBarPageButtonsCount <= 0) {
    start = config.defaultNavigationMin;
    end = Math.min(start + config.maxNavBarPageButtonsCount, maxPagesCount);
    return [start, end];
  }
  if (+postPageCount + halfNavBarPageButtonsCount > maxPagesCount) {
    end = maxPagesCount;
    start = end - config.maxNavBarPageButtonsCount;
    return [start, end];
  }

  return [start, end];
}
