import { useAppSelector } from '../hooks/storeHooks';
import { RootState } from '../store';
import { config } from '../config.d';
import { useParams } from 'react-router-dom';

import { useEffect } from 'react';

import { useAppDispatch } from '../hooks/storeHooks';
import { setPostPageCount } from '../store/postPageCount';
import { Link } from 'react-router-dom';

export function NavBar() {
  const dispatch = useAppDispatch();

  const postPageCount = useParams().postPageCount || 0;
  const [posts] = useAppSelector((state: RootState) => [state.posts.posts, state.searchString.searchString]);
  const pageNumbersCount = posts.length / config.maxPostsQuantity;

  useEffect(() => {
    dispatch(setPostPageCount(+postPageCount));
  }, []);

  const definePagePaginationButtonsIdxs = () => {
    let start = 0;
    if (+postPageCount - Math.floor(config.maxNavBarPageButtonsCount / 2) > 0) {
      start = Math.max(+postPageCount - Math.floor(config.maxNavBarPageButtonsCount / 2) - 1, 0);
    }
    return [start, Math.max(start + 1 + (config.maxNavBarPageButtonsCount - (+postPageCount)), config.maxNavBarPageButtonsCount)];
  }

  return (
    <div className='flex items-center justify-between w-11/12 mx-auto py-4 px-4'>
      <Link to={'/' + Math.max((+postPageCount - 1), 1)} className='text-3xl font-semibold'>Назад</Link>
      <div className='flex items-center justify-center'>
        {
          Array(pageNumbersCount)
            .fill(0)
            .map((_, idx) => idx + 1)
            // .slice(Math.min(+postPageCount - 1, Math.floor(config.maxNavBarPageButtonsCount / 2)), Math.floor((config.maxNavBarPageButtonsCount + (+postPageCount - 1))))
            .slice(definePagePaginationButtonsIdxs()[0], definePagePaginationButtonsIdxs()[1])
            .map(pageCount => {
              return (
                <Link
                  className={`mr-4 font-bold italic text-lg color-${pageCount === +postPageCount ? "green" : "gray"}`}
                  to={'/' + pageCount}>
                  {pageCount}
                </Link>
              );
            })
        }
      </div>
      <Link to={'/' + Math.min((+postPageCount + 1), pageNumbersCount)} className='text-3xl font-semibold'>Далее</Link>
    </div>
  );
}
