import { IPost } from "../types";

export function filterPostsBy(post: IPost, filterBy: string) {
  return post.body.includes(filterBy) || post.title.includes(filterBy) || post.id.toString().includes(filterBy);
}

function prepareValue(value: string) {
  return value.toLowerCase();
}
