import { useAppSelector } from '../hooks/storeHooks';
import { RootState } from '../store';
import { config } from '../config.d';
import { useParams } from 'react-router-dom';

import { useEffect } from 'react';

import { useAppDispatch } from '../hooks/storeHooks';
import { setPostPageCount } from '../store/postPageCount';
import { Link } from 'react-router-dom';
import { filterPostsBy } from '../lib/filterPostsBy';
import { INavigationLimits } from '../types';

interface IProps {
  navigationLimits: INavigationLimits;
  setNavigationLimits: React.Dispatch<React.SetStateAction<INavigationLimits>>;
}

export function NavBar({ navigationLimits, setNavigationLimits }: IProps) {
  const dispatch = useAppDispatch();

  const postPageCount = useParams().postPageCount || 0;
  const [posts, searchString] = useAppSelector((state: RootState) => [state.posts.posts, state.searchString.searchString]);
  const pageNumbersCount = posts.length / config.maxPostsQuantity || 1;

  useEffect(() => {
    dispatch(setPostPageCount(+postPageCount));
  }, []);

  const definePagePaginationButtonsIdxs = () => {
    let [start, end] = [0, 0];

    if (+postPageCount - Math.floor(config.maxNavBarPageButtonsCount / 2) < navigationLimits.min) {
      start = navigationLimits.min;
    } else {
      start = +postPageCount - Math.floor(config.maxNavBarPageButtonsCount / 2);
      setNavigationLimits({ ...max, min: Math.floor(config.maxNavBarPageButtonsCount / 2) });
    }

    if (+postPageCount + Math.floor(config.maxNavBarPageButtonsCount / 2) > navigationLimits.max) {
      end = navigationLimits.max;
    } else {
      end = +postPageCount + Math.floor(config.maxNavBarPageButtonsCount / 2);
    }

    return [start - 1, end + 1];
  }

  return (
    <div className='flex items-center justify-between w-11/12 mx-auto py-4 px-4'>
      <Link to={'/' + Math.max((+postPageCount - 1), 1)} className='text-3xl font-semibold'>Назад</Link>
      <div className='flex items-center justify-center'>
        {
          Array(pageNumbersCount)
            .fill(0)
            .map((_, idx) => idx + 1)
            .slice(definePagePaginationButtonsIdxs()[0] - 1, definePagePaginationButtonsIdxs()[1] - 1)
            .map(pageCount => {
              return (
                <Link
                  className={`mr-4 font-bold italic text-lg color-${pageCount === +postPageCount ? "green" : "gray"}`}
                  to={'/' + pageCount}>
                  {pageCount}
                </Link>
              );
            })
        }
      </div>
      <Link to={'/' + Math.min((+postPageCount + 1), pageNumbersCount)} className='text-3xl font-semibold'>Далее</Link>
    </div>
  );
}
