import { createSlice } from '@reduxjs/toolkit';
import type { PayloadAction } from '@reduxjs/toolkit';
import { IPost } from '../types';

export interface IPostsState {
  posts: IPost[];
};

const initialState: IPostsState = {
  posts: [],
};

const postsSlice = createSlice({
  name: 'posts',
  initialState,
  reducers: {
    filterPosts(state, action: PayloadAction<string>) {
      const filterBy = action.payload;
      state.posts = [...state.posts].filter(post => {
        return post.title.includes(filterBy) || post.body.includes(filterBy) || post.id === +filterBy;
      });
    },
    setPosts(state, action: PayloadAction<IPost[]>) {
      state.posts = action.payload.length ? action.payload : [];
    },
  },
});

export const { setPosts, filterPosts } = postsSlice.actions;
export default postsSlice.reducer;
