import { useEffect, useState } from 'react';
import { useAppSelector } from '../hooks/storeHooks';
import { RootState } from '../store';
import { IPost } from '../types';
import { Post } from './Post';
import { TableHeaderTitle } from './TableHeaderTitle';

import { config } from '../config.d';
import { useParams } from 'react-router-dom';


export function Table() {
  const [shownPosts, setShownPosts] = useState<IPost[]>([]);
  const posts = useAppSelector((state: RootState) => [state.posts.posts, state.searchString.searchString]);
  const postPageCount = useParams().postPageCount || 0;
  const tableHeaderTitles: string[] = [
    'ID', 'Заголовок', 'Описание'
  ];

  useEffect(() => {
    const startIdx = (+postPageCount - 1) * config.maxPostsQuantity;
    const quantity = +postPageCount * config.maxPostsQuantity;

    setShownPosts(posts.slice(startIdx, quantity));
  }, [postPageCount]);

  return (
    <table className="">
      <thead className='bg-[#474955] text-white'>
        <tr className='w-full'>
          {
            tableHeaderTitles.length && tableHeaderTitles.map((title, idx) => {
              return <TableHeaderTitle title={title} key={idx} />;
            })
          }
        </tr>
      </thead>
      <tbody>
        {
          shownPosts.length && shownPosts.map((post, idx) => {
            return <Post post={post} key={idx} />
          })
        }
      </tbody>
    </table>
  );
}
