import { createSlice } from '@reduxjs/toolkit';
import type { PayloadAction } from '@reduxjs/toolkit';

export interface IPostPageCountState {
  postPageCount: number;
};

const initialState: IPostPageCountState = {
  postPageCount: 0,
};

const postPageCountSlice = createSlice({
  name: 'postPageCount',
  initialState,
  reducers: {
    setPostPageCount(state, action: PayloadAction<number>) {
      state.postPageCount = action.payload;
    },
  },
});

export const { setPostPageCount } = postPageCountSlice.actions;
export default postPageCountSlice.reducer;
