import { useAppSelector, useAppDispatch } from '../hooks/storeHooks';

import { setSearchString } from '../store/searchString';
import { RootState } from '../store';

import { SearchIcon } from "./icons/SearchIcon";

import { IPost } from '../types';

export function SearchBar() {
  const dispatch = useAppDispatch();
  const [searchString, posts] = useAppSelector((state: RootState) => [state.searchString.searchString, state.posts.posts]);

  const searchHandler = (e: any) => {
    const filteredPosts: IPost[] = [];
    posts.forEach(post => {
      if (post.title.includes(filterBy) || post.body.includes(filterBy) || post.id === +filterBy) {
        filteredPosts.push(post);
      }
    });

    dispatch(setPosts(filteredPosts));
    dispatch(filterPosts(e.target.value));
    dispatch(setSearchString(e.target.value));
  }

  return (
    <div className="bg-[#5A5C66] w-1/2 my-4 px-7 py-5 flex items-center justify-between">
      <input
        placeholder="Поиск"
        className="bg-inherit w-11/12 text-white"
        value={searchString}
        onChange={searchHandler}
      />
      <SearchIcon />
    </div>
  );
}
