import { ArrowIcon } from "./icons/Arrow";
import { config } from "../config.d";
import { useState } from "react";

interface IProps {
  keyTitlePair: { key: string, title: string };
  setSortBy: React.Dispatch<React.SetStateAction<string>>;
}

export function TableHeaderTitle({ keyTitlePair, setSortBy }: IProps) {
  const { key, title } = keyTitlePair;
  const [rotated, setRotated] = useState<boolean>();

  const clickHandler = () => {
    setSortBy(rotated ? config.defaultSortKey : key)
    setRotated(!rotated);
  }


  return (
    <th className={`p-6 ${title === 'ID' ? 'w-1/12' : 'w-2/5'}`} scope="col">
      <div className="flex items-center justify-center font-semibold">
        <p className="mr-11">{title}</p>
        <ArrowIcon sortKey={key} setSortBy={setSortBy} />
      </div>
    </th>
  );
}
