import { useAppSelector } from '../hooks/storeHooks';
import { RootState } from '../store';
import { config } from '../config.d';
import { useParams } from 'react-router-dom';

import { useEffect } from 'react';

import { useAppDispatch } from '../hooks/storeHooks';
import { setPostPageCount } from '../store/postPageCount';

export function NavBar() {
  const dispatch = useAppDispatch();

  const postPageCount = useParams().postPageCount || 0;
  const posts = useAppSelector((state: RootState) => state.posts.posts);
  const pageNumbersCount = posts.length / config.maxPostsQuantity;
  console.log(posts);

  useEffect(() => {
    dispatch(setPostPageCount(+postPageCount));
  }, []);

  return (
    <div className='flex items-center justify-between w-11/12 mx-auto py-4 px-4'>
      <button className='text-3xl font-semibold'>Назад</button>
      <div className='flex items-center justify-center'>
        {
          Array(pageNumbersCount).fill(0).map((_, idx) => idx + 1).map(pageCount => {
            return <button>{pageCount}</button>
          })
        }
      </div>
      <button className='text-3xl font-semibold'>Далее</button>
    </div>
  );
}
