import { config } from "../../config";
import { useState } from "react";

interface IProps {
  sortKey: string;
  setSortBy: React.Dispatch<React.SetStateAction<string>>;
}

export function ArrowIcon({ sortKey, setSortBy }: IProps) {
  const [rotated, setRotated] = useState<boolean>();

  const clickHandler = () => {
    setSortBy(rotated ? config.defaultSortKey : sortKey)
    setRotated(!rotated);
  }

  return (
    <svg onClick={clickHandler} className={`block cursor-pointer ${rotated ? 'rotate-180' : ''}`} width="12" height="7" viewBox="0 0 12 7" fill="none" xmlns="http://www.w3.org/2000/svg">
      <line x1="0.353553" y1="0.646447" x2="6.18011" y2="6.47301" stroke="#FCFCFC" />
      <line x1="5.64645" y1="6.30331" x2="11.3033" y2="0.646453" stroke="white" />
    </svg>
  );
}
