import { useAppSelector } from '../hooks/storeHooks';
import { RootState } from '../store';

import { useAppDispatch } from '../hooks/storeHooks';

import { setSearchString } from '../store/searchString';

import { SearchIcon } from "./icons/SearchIcon";
import { setPosts } from '../store/posts';

export function SearchBar() {
  const dispatch = useAppDispatch();
  const [posts, searchString] = useAppSelector((state: RootState) => [state.posts.posts, state.searchString.searchString]);

  const searchHandler = (e: any) => {
    const filteredPosts = posts.filter(post => {
      return post.title.includes(searchString) ||
        post.body.includes(searchString) ||
        post.id === +searchString
    });

    dispatch(setPosts(filteredPosts));
    dispatch(setSearchString(e.target.value));
  }

  return (
    <div className="bg-[#5A5C66] w-1/2 my-4 px-7 py-5 flex items-center justify-between">
      <input
        placeholder="Поиск"
        className="bg-inherit w-11/12 text-white"
        value={searchString}
        onChange={searchHandler}
      />
      <SearchIcon />
    </div>
  );
}
